import fetch from 'isomorphic-unfetch'

import Layout from '../../layouts/default'

type JobProps = {
  title: string;
  body?: {
    value: string;
  };
  sections?: [{
    attributes: {
      field_title: string;
      field_body: {
        value: string;
      }
    }
  }]
}

const Job = ({ title, body, sections }: JobProps) => (
  <Layout>
    <div>
      <h1>{title}</h1>
      <div dangerouslySetInnerHTML={{ __html: body?.value }} />
      {
        sections.map(({ attributes }) => (
          <>
            <h2>{attributes.field_title}</h2>
            <div dangerouslySetInnerHTML={{ __html: field_body?.value }} />
          </>
        ))
      }
    </div>
  </Layout>
)

Job.getInitialProps = async (context: any) => {
  const { id } = context.query
  const response = await fetch(`http://127.0.0.1:8888/jsonapi/node/jobs/${id}`)
  const { data } = await response.json()
  const { title, body, field_job_section } = data.attributes

  const newResponse = await fetch(`http://127.0.0.1:8888/jsonapi/field_collection_item/field_job_section/`)
  const { data: sections } = await newResponse.json()

  return { title, body, sections }
}

export default Job
