import fetch from 'isomorphic-unfetch'

import Layout from '../../layouts/default'

import TitleBanner from '../../components/TitleBanner'

type JobProps = {
  title: string,
  sections?: [{
    attributes: {
      field_title: string,
      field_body: {
        value: string
      }
    }
  }]
}

const Job = ({ title, body, sections }: JobProps) => (
  <Layout>
    <TitleBanner title={title} />
    {body && <div dangerouslySetInnerHTML={{ __html: body?.value }} />}
    {
      sections.map(({ attributes }, key) => (
        <section key={key}>
          <h2>{attributes.field_title}</h2>
        </section>
      ))
    }
  </Layout>
)

Job.getInitialProps = async (context: any) => {
  const { id } = context.query

  // fetch `Job`
  const response = await fetch(`${process.env.API_ROOT}/jobs/${id}`)
  const { data } = await response.json()
  const { title, field_job_section } = data.attributes

  // fetch `sections` from field_collection endpoint
  const sectionsResponse = await fetch(`http://127.0.0.1:8888/jsonapi/field_collection_item/field_job_section/`)
  const { data: sections } = await sectionsResponse.json()

  return { title, sections }
}

export default Job
