import fetch from 'isomorphic-unfetch'

import Layout from '../../layouts/default'

import TitleBanner from '../../components/TitleBanner'
import ImageBanner from '../../components/ImageBanner'
import JobListings from '../../components/JobListings'

type JobsIndexProps = {
  title: string,
  body?: string,
  image?: {
    alt: string,
    path: string
  },
  jobs?: []
}

const JobsIndex = ({ jobs, title, body, image }: JobsIndexProps) => (
  <Layout>
    <TitleBanner title={title} />
    <ImageBanner {...image} />
    <JobListings jobs={jobs} />
  </Layout>
)

JobsIndex.getInitialProps = async () => {
  // fetch page data
  const pageResponse = await fetch(`${process.env.API_ROOT}/page/d531df90-0fb9-4475-bdc6-8222b22924c7`)
  const { data: page } = await pageResponse.json()
  const { title, body } = page.attributes

  console.log(body.processed)

  // fetch `jobs` content-types
  const jobsResponse = await fetch(`${process.env.API_ROOT}/jobs/`)
  const { data: jobs } = await jobsResponse.json()

  // fetch `field_image`
  const { field_image } = page.relationships
  const imageResponse = await fetch(`${field_image.links.related.href}`)
  const { data: image } = await imageResponse.json()

  return {
    title,
    body: body.processed,
    image: {
      path: image.attributes.uri.url,
      alt: field_image.data.meta.alt
    },
    jobs
  }
}

export default JobsIndex
