import fetch from 'isomorphic-unfetch'

import Layout from '../../layouts/default'

import TitleBanner from '../../components/TitleBanner'

type JobProps = {
  title: string,
  sections?: [{
    attributes: {
      field_title: string,
      field_body: {
        processed: string
      }
    }
  }]
}

const Job = ({ title, sections }: JobProps) => (
  <Layout>
    <TitleBanner title={title} />
    {
      sections.map(({ attributes }, key) => (
        <section key={key}>
          <h2>{attributes.field_title}</h2>
          <div dangerouslySetInnerHTML={{ __html: attributes.field_body.processed }} />
        </section>
      ))
    }
  </Layout>
)

/**
 * Sets initial props on the default export.
 *
 * @see https://nextjs.org/learn/basics/fetching-data-for-pages
 *
 * @todo use Promise.all() to wrap awaits; be careful of fail-fast
 *
 */
Job.getInitialProps = async (context: any) => {
  const { id } = context.query

  /**
   * Fetch all of the data for the jobs
   */
  const response = await fetch(`${process.env.API_ROOT}/jobs/${id}`)
  const { data } = await response.json()
  const { title, field_job_section } = data.attributes

  //
  const contentIDs = field_job_section.map(({ value }: any) => value)

  // fetch `sections` from field_collection endpoint
  const sectionsResponse = await fetch(`http://127.0.0.1:8888/jsonapi/field_collection_item/field_job_section/`)
  const { data: sections } = await sectionsResponse.json()

  return { title, sections }
}

export default Job
