import fetch from 'isomorphic-unfetch'

import Layout from '../../layouts/default'

type JobProps = {
  title: string;
  body?: {
    value: string;
  };
}

const Job = ({ title, body }: JobProps) => (
  <Layout>
    <div>
      <h1>{title}</h1>
      <div dangerouslySetInnerHTML={{ __html: body?.value }} />
    </div>
  </Layout>
)

Job.getInitialProps = async (context: any) => {
  const { id } = context.query
  const response = await fetch(`http://127.0.0.1:8888/jsonapi/node/jobs/${id}`)
  const { data } = await response.json()
  const { title, body } = data.attributes

  return { title, body }
}

export default Job
