import fetch from 'isomorphic-unfetch'

import Layout from '../../layouts/default'
import Listing from '../../components/Listing'

type ArticlesIndexProps = {
  articles?: [],
}

const ArticlesIndex = ({ articles }: ArticlesIndexProps) => (
  <Layout>
    <h1>Homepage</h1>
    {
      articles.map(({ id, attributes }, key) => 
        <Listing 
          id={id} 
          key={key}
          type={`article`}
          {...attributes}
        />
      )
    }
  </Layout>
)

ArticlesIndex.getInitialProps = async () => {
  const response = await fetch('http://127.0.0.1:8888/jsonapi/node/article/')
  const { data } = await response.json()

  return { articles: data }
}

export default ArticlesIndex
