import fetch from 'isomorphic-unfetch'

import Layout from '../../layouts/default'
import Article from '../../components/Article'

type NewsIndexProps = {
  articles?: [];
}

const NewsIndex = ({ articles }: NewsIndexProps) => (
  <Layout>
    <h1>Homepage</h1>
    {
      articles.map(({ id, attributes }, key) => 
        <Article 
          id={id} 
          key={key}
          {...attributes}
        />
      )
    }
  </Layout>
)

NewsIndex.getInitialProps = async () => {
  const response = await fetch('http://127.0.0.1:8888/jsonapi/node/article/')
  const { data } = await response.json()

  return { articles: data }
}

export default NewsIndex
