import fetch from 'isomorphic-unfetch'

import Layout from '../../layouts/default'

import TitleBanner from '../../components/TitleBanner'
import Listing from '../../components/Listing'

type JobsIndexProps = {
  title: string,
  body?: string,
  jobs?: []
}

const JobsIndex = ({ jobs, title }: JobsIndexProps) => (
  <Layout>
    <TitleBanner title={title} />
    {
      jobs.map(({ id, attributes }, key) =>
        <Listing
          type={`job`}
          id={id}
          key={key}
          {...attributes}
        />
      )
    }
  </Layout>
)

JobsIndex.getInitialProps = async () => {
  // fetch page data
  const pageResponse = await fetch('http://127.0.0.1:8888/jsonapi/node/page/d531df90-0fb9-4475-bdc6-8222b22924c7')
  const { data: page } = await pageResponse.json()
  const { title, body } = page.attributes

  // fetch `jobs` content-types
  const jobsResponse = await fetch('http://127.0.0.1:8888/jsonapi/node/jobs/')
  const { data: jobs } = await jobsResponse.json()

  // fetch `field_image`
  const { field_image } = page.relationships
  const imageResponse = await fetch(`${field_image.links.related.href}`)
  const { data: image } = await imageResponse.json()

  return {
    title,
    body,
    image: image.uri.url,
    jobs
  }
}

export default JobsIndex
