import Link from 'next/link'
import cx from 'classnames'

type ListingViewProps = {
  id: string,
  type: 'job' | 'article',
  title: string,
  body?: string,
  className?: string
}

export const ListingView = ({ id, type, title, body, className }: ListingViewProps) => {
  const classNames = cx('c-listing-item', {
    [className]: className
  })

  return (
    <article className={classNames}>
      <h3 className='c-listing-item__title'>
        <Link href={`/${type}s/[id]`} as={`/${type}s/${id}`}>
          <a>{title}</a>
        </Link>
      </h3>
      {
        body &&
          <div
            className='c-listing-item__body'
            dangerouslySetInnerHTML={{ __html: body?.processed }}
          />
      }
    </article>
  )
}

export default ListingView
